(69, 'habit-moment-survey', 'marketing', 'en_US', 'Were all ears! Share your experience so far with WooCommerce', 'Wed love your input to shape the future of WooCommerce together. Feel free to share any feedback, ideas or suggestions that you have.', '{}', 'pending', 'woocommerce.com', '2023-04-27 04:31:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(70, 'pinterest_03_2022_update', 'marketing', 'en_US', 'Your Pinterest for WooCommerce plugin is out of date!', 'Update to the latest version of Pinterest for WooCommerce to continue using this plugin and keep your store connected with Pinterest. To update, visit <strong>Plugins &gt; Installed Plugins</strong>, and click on update now under Pinterest for WooCommerce.', '{}', 'pending', 'woocommerce.com', '2023-04-27 04:31:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(71, 'wc-admin-wisepad3', 'marketing', 'en_US', 'Take your business on the go in Canada with WooCommerce In-Person Payments', 'Quickly create new orders, accept payment in person for orders placed online, and automatically sync your inventory  no matter where your business takes you. With WooCommerce In-Person Payments and the WisePad 3 card reader, you can bring the power of your store anywhere.', '{}', 'pending', 'woocommerce.com', '2023-04-27 04:31:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(72, 'google_listings_ads_custom_attribute_mapping_q4_2022', 'marketing', 'en_US', 'Our latest improvement to the Google Listings & Ads extension: Attribute Mapping', 'You spoke, we listened. This new feature enables you to easily upload your products, customize your product attributes in one place, and target shoppers with more relevant ads. Extend how far your ad dollars go with each campaign.', '{}', 'unactioned', 'woocommerce.com', '2023-04-27 04:31:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(73, 'google_listings_ads_pmax_i1_q1_2023_no_gla', 'marketing', 'en_US', 'Create more engaging ads  without the hard work', 'Get in front of millions of shoppers searching for products like yours with Google Listings &amp; Ads. With new customization features, Google automatically tests multiple combinations of text and images to create the most engaging ad to boost your business. Plus, get up to $500 in ad credit  terms and conditions apply.', '{}', 'pending', 'woocommerce.com', '2023-04-27 04:31:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(74, 'google_listings_ads_pmax_i1_q1_2023_with_gla', 'marketing', 'en_US', 'New customization features to boost your business', 'You can now add custom images, messaging, and URLs to campaigns in Google Listings &amp; Ads. Google then automatically tests multiple combinations to create the most engaging version to help boost your business. Get more sales with dynamic content  edit an existing campaign or create a new ad now.', '{}', 'unactioned', 'woocommerce.com', '2023-04-27 04:31:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(75, 'wc_admin_BNPL_WCPay_coming-soon_Q1_2023', 'marketing', 'en_US', 'Buy now, pay later is heading to WooCommerce Payments', 'Great news: full integration with Afterpay and Affirm is on its way to WooCommerce Payments! Youll soon be able to increase your order values, sell more, and offer customers a payment option theyll love  with no extra downloads or extensions required.', '{}', 'pending', 'woocommerce.com', '2023-04-27 04:31:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(76, 'tap_to_pay_iphone_q2_2023_no_wcpay', 'marketing', 'en_US', 'Accept in-person contactless payments on your iPhone', 'Tap to Pay on iPhone and WooCommerce Payments is quick, secure, and simple to set up  no extra terminals or card readers are needed. Accept contactless debit and credit cards, Apple Pay, and other NFC digital wallets in person.', '{}', 'unactioned', 'woocommerce.com', '2023-04-27 04:31:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(77, 'tap_to_pay_iphone_q2_2023_with_wcpay', 'marketing', 'en_US', 'New: accept in-person contactless payments on your iPhone', 'Tap to Pay on iPhone is quick, secure, and simple to set up in WooCommerce Payments  no extra terminals or card readers are needed. Accept contactless debit and credit cards, Apple Pay, and other NFC digital wallets in person in a few short steps!', '{}', 'pending', 'woocommerce.com', '2023-04-27 04:31:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(78, 'wc-admin-wcpay-denmark-Q2-2023', 'marketing', 'en_US', 'WooCommerce Payments is now available in Denmark!', 'Weve recently released WooCommerce Payments in Denmark. You can view and manage transactions right in your WordPress dashboard while securely accepting major cards, Apple Pay, and payments in over 100 currencies.', '{}', 'pending', 'woocommerce.com', '2023-04-27 04:31:06', NULL, 0, 'plain', '', 0, 0, 'info'),
